mapper.select:
SELECT DATE_FORMAT(`trans_time`, '%Y-%m') AS month,
       SUM(`amount`)  AS totalAmount
FROM T_TRANSACTION
WHERE `type` = #{transType}
AND  YEAR (`trans_time`) = #{year}
AND `username` = #{username}
GROUP BY month
ORDER BY month

SELECT SUM(CASE WHEN type = 1 THEN amount ELSE 0 END) AS totalIncome,
       SUM(CASE WHEN type = 2 THEN amount ELSE 0 END) AS totalExpense,
       SUM(CASE WHEN type = 3 THEN amount ELSE 0 END) AS totalBoth
FROM T_TRANSACTION
WHERE YEAR (trans_time) = #{year}
  AND `username` = #{username}
